import "./profile.css" 
import Topbar from "../../components/topbar/Topbar";
import Sidebar from "../../components/sidebar/Sidebar";
import Feed from "../../components/feed/Feed";
import Rightbar from "../../components/rightbar/Rightbar";
import { useEffect, useState } from "react";
import axios from "axios";
import {useParams} from "react-router";

//there's some issue with the loading of the profile page mainly due to "Feed"
//the problem lies with parsing issues within the source code of 'timeago.js' and 'axios'

export default function Profile() {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const [user, setUser] = useState({});
    const username = useParams().username;

    useEffect(() =>{
        const fetchUser = async () =>{
        const res = await axios.get(`/users?username=${username}`);
        setUser(res.data);
        };
        fetchUser();
    },[username])

    return (
        <>
        <Topbar/>
        <div className="profile">
            <Sidebar/>
            <div className="profileRight">
                <div className="profileRightTop">
                    <div className="profileCover">
                        <img src= {user.coverPicture || PF + "post/noCover.png"} alt="" className="profileCoverImg" />
                        <img src= {user.profilePicture || PF + "person/noAvatar.png"} alt="" className="profileUserImg" />
                    </div>
                    <div className="profileInfo">
                        <h4 className="profileInfoName">{user.username}</h4>
                        <span className="profileInfoDesc">{user.desc}</span>
                    </div>
                </div>
                <div className="profileRightBottom">
                    <Feed username={username}/>
                    <Rightbar user={user}/>
                </div>
            </div>

        </div>
        </>
    );
}
